import React, {Component, createContext} from 'react';
import {menuItems, detailProduct} from './data';


export const DataContext = createContext();

class DataContextProvider extends Component {
    state = {
        products:[...menuItems],
        detailProduct: detailProduct,
        num:null
    }


showItems =(id)=> {
    const item = this.state.products.find(item=>item.id===id)
    return item;
}

handleDetail = (id) => {
    const item = this.showItems(id);
    this.setState(()=>{
        return {
            detailProduct:item
        }
    })
}
generateNum = () => {
    const num = Math.floor(Math.random()*9)+1;
    this.setState(()=>{
        return{
            num:num
        }
    })
}

generateTitle = () => {
    const item = this.state.products.id;
    const number = this.generateNum(number);
    if (item==number){
        return(
            this.state.products.title
        )
    }

} 


render(){
    return(
        <DataContext.Provider value = {{...this.state, handleDetail:this.handleDetail, generateNum:this.generateNum, generateTitle:this.generateTitle}}>
            {this.props.children}
        </DataContext.Provider>
    )
}
}

export default DataContextProvider;
